_dom.findId("Bug_AssignedUser_Id").onchange(function(n){var t=n.data("bugId"),i=n.val();_ajax.post("/api/v1/bugs/"+t+"/assign",{id:i},function(n,t){n==200?_notify.showSuccess("Bug assigned to "+(t.assignedUser?t.assignedUser.name:"no one")):_notify.showError("Could not assign bug to user")})});_dom.findId("action-close").onclick(function(n){var t=n.data("bugId");_ajax.post("/api/v1/bugs/"+t+"/close",{},function(t,i){t==200?(_notify.showSuccess("Bug closed"),_dom.findId("bug-status").element.textContent="("+i.status+")",n.classAdd("hide")):_notify.showError("Could not close bug due to error")})});